            .addExercise (
                this.state.name,
                this.state.exerType,
                this.state.totalTime,
                this.state.totalReps,
                this.state.date,
                this.state.owner,
            )
            .then((response) => {
                console.log(response)
            })
            .catch((err) => console.error(err));
    };

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };


