            <Form className="form" onSubmit={this.handleFormSubmit}>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label htmlFor="name">name</Form.Label>
                        <Form.Control type="text" name="name" placeholder="exercise name" value={this.state.name} onChange={(event) => this.handleChange(event)} />
                    </Form.Group>

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label htmlFor="exerType">type</Form.Label>
                        <Form.Control type="text" name="exerType" placeholder="exercise type" value={this.state.exerType} onChange={(event) => this.handleChange(event)} />
                    </Form.Group>

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label htmlFor="totalTime">total time</Form.Label>
                        <Form.Control type="number" name="totalTime" placeholder="in seconds" value={this.state.totalTime} onChange={(event) => this.handleChange(event)} />
