            .addMetrics (
                this.state.weight,
                this.state.shoulders,
                this.state.abs,
                this.state.cuadriceps,
                this.state.date,
                this.state.owner,
            )
            .then((response) => {
                console.log(response)
            })
            .catch((err) => console.error(err));
    };

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };


