import React from 'react'

import '../styles/FormExercise.css'

import { Link } from 'react-router-dom';

import MetricsService from '../services/MetricsService'

class FormMetrics extends React.Component {

    state = {
        weight: '',
        shoulders: '',
        abs: '',
        cuadriceps: '',
        date: '',
        owner: ''
    };

  service = new MetricsService()


  handleFormSubmit = (event) => {
        event.preventDefault();

        const getDate = ()=>{
            var today = new Date();
            var dd = String(today.getDate()).padStart(2, '0');
            var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
            var yyyy = today.getFullYear();
            today = mm + '/' + dd + '/' + yyyy;
            return today
        }
        const todayDate = getDate()

        this.setState({date: todayDate, owner: this.props.loggedInUser._id})

        this.service
            .addMetrics (
                this.state.weight,
                this.state.shoulders,
                this.state.abs,
                this.state.cuadriceps,
                this.state.date,
                this.state.owner,
            )
            .then((response) => {
                console.log(response)
            })
            .catch((err) => console.error(err));
    };

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };


  render() {
    return (
        <div>
            <h2>New metrics entry</h2>
            <form onSubmit={this.handleFormSubmit}>
                <label htmlFor="weight">Weight: </label>
                <input
                    type="number"
                    name="weight"
                    value={this.state.weight}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="shoulders">shoulders: </label>
                <input
                    type="number"
                    name="shoulders"
                    value={this.state.shoulders}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="abs">abs: </label>
                <input
                    type="number"
                    name="abs"
                    value={this.state.abs}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="cuadriceps">cuadriceps: </label>
                <input
                    type="number"
                    name="cuadriceps"
                    value={this.state.cuadriceps}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <button type="submit">add entry</button>
            </form>
        </div>
    );
}
}

export default FormMetrics
