  // }

  // porLeer = (mangaID, userID) => {
  //   return this.service.post("/porLeer", {mangaID, userID})
  //   .then(response => response.data)
  // }

  getUser = (userID) => {
    return this.service.get(`/getUser/${userID}`, {userID})
    .then(response => response.data)
  }

}

export default ExerciseService;
