import React from 'react'

import { Link } from 'react-router-dom';

import ExerciseService from '../services/ExerciseService';

class DetailsExercise extends React.Component{

  state = {
    exerciseInfo: {}
  }

  service = new ExerciseService()

  // addToLeidos = ()=>{
  //   this.service.leidos(this.props.match.params.id, this.props.loggedInUser._id)
  //   .then((result) => {
  //     console.log(result);
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   });
  // }

  renderButtons = ()=>{
    if(this.props.loggedInUser){
      return(
        <div>
          <button onClick={()=>this.addToLeidos()}>Añadir a LEIDOS</button>
          <button onClick={()=>this.addToLeyendo()}>Añadir a LEYENDO</button>
          <button onClick={()=>this.addToPorLeer()}>Añadir a POR LEER</button>          
        </div>
      )
    } else {
      return(
        <div>
          <Link to="/signup"><button>Sign Up</button></Link>
          <Link to='/login'><button>Log In</button>    </Link>

        </div>
      )
    }
  }

  componentDidMount(){
    fetch(`https://api.jikan.moe/v3/manga/${this.props.match.params.id}`)
    .then((data)=>{
      return data.json()
    })
    .then((dataJSON)=>{
      this.setState({mangaInfo: dataJSON})
    })
    .catch((err)=>{
      console.log(err)
    })
  }

  render(){
    return(
      <div>
        <h2>{this.state.mangaInfo.title_english}</h2>
        <img src={this.state.mangaInfo.image_url} alt={this.state.mangaInfo.title_english}/>
        {this.renderButtons()}
      </div>
    )    
  }
}

export default DetailsExercise
