import axios from "axios";


class ExerciseService {

  constructor() {
    let service = axios.create({
      baseURL: "http://localhost:3000", //=> CAMBIAR POR EL ENLACE DE MI BACKEND EN HEROKU
      withCredentials: true
    });


    this.service = service;
  }

  getAllExercises = (userID) => {
    return this.service.post("/get-all-exercises", {userID})
    .then(response => response.data)
  }

  addExercise = (name, exerType, totalTime, totalReps, date, owner) => {
    return this.service.post("/create-exercise", {
      name, 
      exerType, 
      totalTime, 
      totalReps, 
      date, 
      owner
    })
    .then(response => response.data)
  }

}

export default ExerciseService;
