import React from 'react'

import '../styles/AllExercises.css'

import { Link } from 'react-router-dom';

import ExerciseService from '../services/ExerciseService'

class AllExercises extends React.Component {

  state = {
    exercises: []
  }

  service = new ExerciseService()

  componentDidMount(){
    this.service
    .getAllExercises(this.props.loggedInUser._id) 
    .then((data)=>{
      return data.json()
    })
    .then((dataJSON)=>{
      this.setState({exercises: dataJSON.results})
    })
    .catch((err)=>{
      console.log(err)
    })
  }

  renderExercises = ()=>{
    return this.state.exercises.map((exercise, index)=>{
      return(
        <Link to={`/all-exercises/${exercise._id}`} key={index}>
          <div className="exercise-container">
            <h5>{exercise.name}</h5>
            <p>{exercise.exerType}</p>
          </div>
        </Link>
      )
    })
  }

  renderLoadingImage = ()=>{
    return <img src="" alt="Loading"/> //=> url SPINNER DE CARGA
  }

  render(){
    return(
      <div className="AllExercises">
        <Button>
          <Link to="/create-exercise">add new exercise</Link>
        </Button>
        <h2>Your workouts</h2>
        <div className="all-exercises-container">
          {this.state.exercises.length === 0 ? this.renderLoadingImage() : this.renderExercises()}          
        </div>
      </div>
    )    
  }
}

export default AllExercises
