import React from 'react'

import '../styles/FormExercise.css'

import { Link } from 'react-router-dom';

import ExerciseService from '../services/ExerciseService'

class FormExercise extends React.Component {

    state = {
        name: '',
        notes: '',
        exerType: '',
        totalTime: '',
        totalReps: '',
        date: '',
        owner: ''
    };

  service = new ExerciseService()


  handleFormSubmit = (event) => {
        event.preventDefault();

        const getDate = ()=>{
            var today = new Date();
            var dd = String(today.getDate()).padStart(2, '0');
            var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
            var yyyy = today.getFullYear();
            today = mm + '/' + dd + '/' + yyyy;
            return today
        }
        const todayDate = getDate()

        this.setState({date: todayDate, owner: this.props.loggedInUser._id})
        this.service
            .addExercise (
                this.state.name,
                this.state.exerType,
                this.state.totalTime,
                this.state.totalReps,
                this.state.date,
                this.state.owner,
            )
            .then((response) => {
                console.log(response)
            })
            .catch((err) => console.error(err));
    };

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };


  render() {
    return (
        <div>
            <h2>New exercise</h2>
            <form onSubmit={this.handleFormSubmit}>
                <label htmlFor="name">Name: </label>
                <input
                    type="text"
                    name="name"
                    value={this.state.name}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="exerType">Type: </label>
                <input
                    type="text"
                    name="exerType"
                    value={this.state.exerType}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="totalTime">total time: </label>
                <input
                    type="text"
                    name="totalTime"
                    value={this.state.totalTime}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <label htmlFor="totalReps">total reps: </label>
                <input
                    type="text"
                    name="totalReps"
                    value={this.state.totalReps}
                    onChange={(event) => this.handleChange(event)}
                />
                <br />
                <button type="submit">Create</button>
            </form>
        </div>
    );
}
}

export default FormExercise
